primary key(città, provincia, via, numero_civico)
);*/

CREATE TABLE libro(
ISBN varchar(14),
tipo varchar(10) not null,
anno_pubblicazione int not null,
numero_pagine int not null,
prezzo int not null,
numero_disponibili int ,
descrizione mediumtext not null,
autore varchar (50),
titolo text not null,
copertina varchar(50) not null unique,
FULLTEXT KEY (`titolo`),
FULLTEXT KEY (`titolo`,`descrizione`),
primary key (ISBN)
);

CREATE TABLE categoria(
id int auto_increment,
nome varchar(30) not null,
descrizione mediumtext not null,
primary key(id)
);

create table ordini(
id_utente int(11),
oradiordine timestamp not null,
id int primary key auto_increment,
ISBN varchar(14) not null,
tipo varchar(10) not null,
anno_pubblicazione int not null,
numero_pagine int not null,
prezzo int not null,
descrizione mediumtext not null,
autore varchar (50) not null,
titolo text not null,
copertina varchar(50) not null,
quantita int,
totale int
);

CREATE TABLE `login` (
  `id` char(36) NOT NULL,
  `idutente` int(11) NOT NULL,
  `token` char(36) NOT NULL,
  `time` timestamp NOT NULL,
  PRIMARY KEY (`id`),
  KEY (`idutente`),
  CONSTRAINT FOREIGN KEY (`idutente`) REFERENCES `utente` (`id`)
);

CREATE TABLE libro_categoria(
ISBN varchar(14),
id int,
FOREIGN KEY (id) REFERENCES categoria(id) on update cascade on delete cascade,
FOREIGN KEY (isbn) REFERENCES libro(isbn) on update cascade on delete cascade,
primary key(isbn,id)
);

CREATE TABLE libro_preferito(
ISBN varchar(14),
id int,
FOREIGN KEY (id) REFERENCES utente(id) on update cascade on delete cascade,
FOREIGN KEY (isbn) REFERENCES libro(isbn) on update cascade on delete cascade,
primary key(isbn,id)
);

CREATE TABLE carrello(
ISBN varchar(14),
id int,
FOREIGN KEY (id) REFERENCES categoria(id) on update cascade on delete cascade,
FOREIGN KEY (isbn) REFERENCES libro(isbn) on update cascade on delete cascade,
primary key(isbn,id)
);

